import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Header = () => {
    const location = useLocation(); // Avoids needing to pass current path to App.js

    const getButtons = () => {
        switch(location.pathname) {
            case '/':
                return [
                    { to: '#', label: 'Open About Modal' },
                    { to: '#', label: 'Open Contact Modal' },
                ];
            case '/skills':
                return [
                    { to: '/projects', label: 'Skills to Projects Page' },
                    { to: '/', label: 'Skills to Home Page' },
                ];
            case '/projects':
                return [
                    { to: '/skills', label: 'Projects to Skills Page' },
                    { to: '/', label: 'Projects to Home Page' },
                ];
            default:
                return [];
        }
    };

    const buttons = getButtons();


