import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Header = () => {
    const location = useLocation(); // Avoids needing to pass current path to App.js

    const getButtons = () => {
        switch(location.pathname) {
            case '/':
                return [
                    { to: '#', label: 'Open About Modal' },
                    { to: '#', label: 'Open Contact Modal' },
                ];
            case '/skills':
                return [
                    { to: '/projects', label: 'Skills to Projects Page' },
                    { to: '/', label: 'Skills to Home Page' },
                ];
            case '/projects':
                return [
                    { to: '/skills', label: 'Projects to Skills Page' },
                    { to: '/', label: 'Projects to Home Page' },
                ];
            default:
                return [];
        }
    };

    const buttons = getButtons();


    return (
        <header className='flex justify-between p-5 w-full items-center'>
            <div className='flex flex-row gap-x-5 items-center'>
                <h1 className='text-right font-bold lg:text-4xl sm:text-2xl'>
                    Tanmay<br/>Gupta
                </h1>
                <div className="border-l-4 border-black h-14 rounded-t-xl rounded-b-xl"></div>
                <h1 className='text-right font-bold lg:text-2xl sm:text-xl'>
                    Software<br/>Engineer
                </h1>
            </div>
            <div>
                <p>hi</p>
            </div>
        </header>
    );

};

export default Header;
