import { useState } from "react";
import CustomButton from "./CustomButton";
import customButton from './customButton.css';

const EmptyPanel = ({ title, subtitle, image = [], desc = [] }) => {

    const [currentIndex, setCurrentIndex] = useState(0);

    const handlePrev = () => {
        setCurrentIndex( (prevIndex) =>
            prevIndex === 0 ? image.length - 1 : prevIndex - 1
        );
    };

    const handleNext = () => {
        setCurrentIndex( (prevIndex) =>
            prevIndex === image.length - 1 ? 0 : prevIndex + 1
        );
    };

    // Splits descriptions by newline characters to allow breaks:
    const descriptionLines = desc[currentIndex]?.split('\n');


    return (
        <div
            className="py-5 px-5 flex flex-col"
            style={{background:'#FFF1D8', border:'3px solid black', borderRadius:'20px'}}
        >
            <div className="text-start font-bold text-xl lg:text-2xl">
                {title}
            </div>
            <div className="text-start font-base text-lg lg:text-xl">
                {subtitle}
            </div>
            <div className="mt-5 flex flex-row items-center justify-between">
                <button
                    onClick={handlePrev}
                    className="custom-button yellow pi pi-chevron-left items-center px-5"
                    style={{padding:"15px"}}
                />
                <img
                    src={image[currentIndex]}
                    alt={`Image ${currentIndex + 1}`}
                    style={{
                        maxHeight: "200px", 
                        borderRadius: "10px", 
                        width: "100%",          // Make image take up 100% of its container's width
                        maxWidth: "50%",       // Prevent image from being wider than its container
                        height: "auto",         // Keep aspect ratio intact
                        objectFit: "contain",   // Ensure it fits inside the container without distortion
                    }}
                />
                <button
                    onClick={handleNext}
                    className="custom-button yellow pi pi-chevron-right items-center px-5"
                    style={{padding:"15px"}}
                />
            </div>
            <div
                className="mt-5 lg:text-lg text-base"
                style={{
                    maxHeight: '150px',
                    overflowY: 'auto', // Vertical scrolling for overflow.
                    wordWrap: 'break-word', // Breaks long words.
                    textOverflow: 'ellipsis', // Adds ellipsis for overflowing text.
                }}
            >
                {descriptionLines?.map((line, index) => (
                    <p key={index}>{line}</p>
                ))}
            </div>
        </div>
    );
};

export default EmptyPanel;
