import EmptyPanel from "../components/EmptyPanel";
import CustomButton from '../components/CustomButton';
import { useState } from "react";

const Projects = () => {

    const [activeFilters, setActiveFilters] = useState([]); // Sets an array
    const [buttonStates, setButtonStates] = useState({ // Sets keys
        JavaScript: 'yellow',
        Python: 'yellow',
        PHP: 'yellow',
        'C#': 'yellow',
    });

    // Change button colour:
    const toggleFilter = (filter) => {
        // Toggle button
        setButtonStates( (prev) => ({
            ...prev,
            [filter]: prev[filter] === "yellow" ? "grey" : "yellow",
        }) );

        // Add/remove filter
        setActiveFilters( (prev) =>
            prev.includes(filter)
                ? prev.filter((f) => f !== filter)
                : [...prev, filter]
        );
    };

    const panels = [
        {
            title: "Pokémon Game",
            subtitle: "JavaScript, PHP, React.js, Laravel",
            tags: ["JavaScript", "PHP"],
        },
        {
            title: "Gradient Designer - Art Generator",
            subtitle: "Python, Data Analysis",
            tags: ["Python"],
        },
        {
            title: "Portfolio Website",
            subtitle: "React.js, JavaScript, CSS",
            tags: ["JavaScript"],
        },
        {
            title: "Finance Tracker",
            subtitle: "C#, ASP.NET",
            tags: ["C#"],
        },
    ];

    const filteredPanels = panels.filter( (panel) =>
        activeFilters.length === 0
            ? true
            : panel.tags.some((tag) => activeFilters.includes(tag))
    );



    return (
        <div>
            <div className="items-center my-5 ml-5 lg:ml-20 flex lg:flex-row flex-col gap-5 w-fit">
                <p className="items-center font-bold flex flex-col">
                    <p>Filter</p>
                    <p>Projects</p>
                </p>
                { ["JavaScript", "Python", "PHP", "C#"].map( (label) => (
                    <CustomButton
                        key={label}
                        colour={buttonStates[label]}
                        label={label}
                        onClick={ () => toggleFilter(label) }
                    />
                ) )}

            </div>
            <div className="my-5 flex flex-col items-center h-screen">

                <div className="w-full px-5 lg:w-3/4 lg:mx-0 flex flex-col gap-y-5">
                    {filteredPanels.map( (panel, index) => (
                        <EmptyPanel
                            key={index}
                            title={panel.title}
                            subtitle={panel.subtitle}
                        />
                    ) )}
                </div>
            </div>
        </div>
    );
};

export default Projects;
