import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';

import { Button } from 'primereact/button';
import 'primeicons/primeicons.css';


const Header = () => {
    const location = useLocation(); // Avoids needing to pass current path to App.js

    const getButtons = () => {
        switch(location.pathname) {
            case '/':
                return [
                    { to: '#', label: 'About' },
                    { to: '#', label: 'Contact' },
                ];
            case '/skills':
                return [
                    { to: '/projects', label: 'Projects', icon: 'pi pi-graduation-cap' },
                    { to: '/', label: 'Home' },
                ];
            case '/projects':
                return [
                    { to: '/skills', label: 'Skills', icon: 'pi pi-lightbulb' },
                    { to: '/', label: 'Home', icon: 'pi pi-home' },
                ];
            default:
                return [];
        }
    };

    const buttons = getButtons();


    const [isClicked, setIsClicked] = useState(false);

    const handleMouseEnter = () => {
        setIsClicked(false); // Reset click state on hover
    };

    const handleMouseLeave = () => {
        setIsClicked(false); // Reset click state when leaving
    };

    const handleClick = () => {
        setIsClicked(true); // Set clicked state
    };

    const buttonStyle = {
        borderRadius: '10px',
        padding: '10px 20px',
        fontWeight: 'bold',
        backgroundColor: isClicked ? '#555' : '#ccc', // Change color on click
        boxShadow: isClicked ? 'none' : '4px 4px 0px rgba(0, 0, 0, 0.3)', // Remove shadow on click
        transition: 'all 0.3s ease', // Smooth transition for hover/click
    };

    const hoverStyle = {
        ...buttonStyle,
        backgroundColor: '#aaa', // Darker color on hover
        boxShadow: '4px 4px 0px rgba(0, 0, 0, 0.3)', // Add shadow on hover
    };




    return (
        <header className='flex justify-between p-10 py-7 w-full items-center border-b-4 border-black' style={{background:'#A2D8B4'}}>
            <div className='flex flex-row gap-x-5 items-center'>
                <h1 className="text-right font-bold text-xl sm:text-2xl lg:text-4xl">
                    Tanmay<br/>Gupta
                </h1>
                <div className="w-[5px] bg-black self-stretch rounded-lg"></div>
                <h1 className="text-right font-bold text-sm sm:text-xl lg:text-2xl">
                    Software<br/>Engineer
                </h1>
            </div>
            <div>
                <nav className="flex flex-row gap-4">
                    {buttons.map((btn, index) => (
                        <Link key={index} to={btn.to} >
                            <Button>hi</Button>
                        </Link>
                    ))}
                </nav>
            </div>
        </header>
    );

};

export default Header;
