const Footer = () => {
    return (
        <footer className="w-full absolute bottom-0 bg-gray-200 flex items-end h-40">
            <div className="flex justify-center w-full gap-4"> {/* Ensures spacing */}
                {Array.from({ length: 21 }).map((_, index) => {
                    const angle = (index - 10) * -10; // Adjust angle (-100 to +100 degrees)
                    return (
                        <div
                            key={index}
                            className="bg-black"
                            style={{
                                width: "2px", // Line thickness
                                height: "100%", // Stretch to the full footer height
                                transform: `rotate(${angle}deg)`,
                                transformOrigin: "bottom center", // Pivot lines at the bottom
                            }}
                        ></div>
                    );
                })}
            </div>
        </footer>

    );
};

export default Footer;
